package jwt

import (
	"context"
	"crypto/ed25519"
	"encoding/hex"
	"fmt"
	"log/slog"

	"github.com/golang-jwt/jwt/v5"

	"github.com/smartcontractkit/chainlink-common/pkg/nodeauth/utils"
	p2ptypes "github.com/smartcontractkit/libocr/ragep2p/types"
)

// NodeJWTAuthenticator is designed to be used by the server-side service to authenticate the JWT token generated by the Node.
//  1. Standard JWT Validation: validate the JWT claims and signature against the node's public key.
//  2. Public Key Validation: validate the node's public key against the topologyProvider cache/on-chain.
type NodeJWTAuthenticator struct {
	nodeAuthProvider NodeAuthProvider // Node topology source of truth to validate node validity.
	parser           *jwt.Parser      // JWT parser to parse the JWT token.
	logger           *slog.Logger
}

func NewNodeJWTAuthenticator(nodeAuthProvider NodeAuthProvider, logger *slog.Logger) *NodeJWTAuthenticator {
	// Configure parser with validation options
	parser := jwt.NewParser(
		jwt.WithIssuedAt(),                      // Validate issued at time
		jwt.WithExpirationRequired(),            // Require expiration time
	)

	return &NodeJWTAuthenticator{
		nodeAuthProvider: nodeAuthProvider,
		parser:           parser,
		logger:           logger,
	}
}

// 1. Standard JWT Validation: validate the JWT claims and signature against the node's public key.
// 2. Public Key Validation: validate the node's public key against the topologyProvider cache/on-chain.
func (v *NodeJWTAuthenticator) AuthenticateJWT(ctx context.Context, tokenString string, originalRequest any) (bool, error) {
	// Parse JWT claims
	claims, err := v.parseJWTClaims(tokenString)
	if err != nil {
		return false, fmt.Errorf("failed to parse and validate JWT claims: %w", err)
	}

	// Extract public key from JWT claim
	publicKey, err := v.decodePublicKey(claims.PublicKey)
	if err != nil {
		return false, fmt.Errorf("invalid public key format: %w", err)
	}

	// Extract p2pId from JWT claim
	p2pId, err := v.decodeP2PId(claims.P2PId)
	if err != nil {
		return false, fmt.Errorf("invalid p2pId format: %w", err)
	}

	// Verify JWT signature against the public key
	if err := v.verifyJWTSignature(tokenString, publicKey); err != nil {
		return false, fmt.Errorf("JWT signature verification failed: %w", err)
	}

	// Verify request digest integrity
	if err := v.verifyRequestDigest(claims, originalRequest); err != nil {
		return false, fmt.Errorf("request integrity check failed: %w", err)
	}

	// Public Key Validation: Verify node's pubkey and p2pId against the whitelisted registry via NodeAuthProvider.
	isValid, err := v.nodeAuthProvider.IsNodePubKeyTrusted(ctx, p2pId, publicKey)
	if err != nil {
		v.logger.Error("Node validation failed",
			"p2pId", claims.P2PId,
			"error", err,
		)
		return false, fmt.Errorf("node validation failed: %w", err)
	}

	if !isValid {
		v.logger.Warn("Unauthorized node attempted access",
			"p2pId", claims.P2PId,
		)
		return false, fmt.Errorf("unauthorized node: %s", claims.P2PId)
	}

	v.logger.Debug("JWT validation successful",
		"p2pId", claims.P2PId,
		"environment", claims.Environment,
	)

	return true, nil
}

// parseJWTClaims extracts JWT claims from the token string.
func (v *NodeJWTAuthenticator) parseJWTClaims(tokenString string) (*NodeJWTClaims, error) {
	token, _, err := v.parser.ParseUnverified(tokenString, &NodeJWTClaims{})
	if err != nil {
		return nil, fmt.Errorf("invalid JWT format: %w", err)
	}

	claims, ok := token.Claims.(*NodeJWTClaims)
	if !ok {
		return nil, fmt.Errorf("invalid claims type")
	}
	return claims, nil
}

// decodePublicKey converts hex-encoded public key in the JWT claim to ed25519.PublicKey
func (v *NodeJWTAuthenticator) decodePublicKey(publicKeyHex string) (ed25519.PublicKey, error) {
	publicKeyBytes, err := hex.DecodeString(publicKeyHex)
	if err != nil {
		return nil, fmt.Errorf("invalid hex encoding: %w", err)
	}

	if len(publicKeyBytes) != ed25519.PublicKeySize {
		return nil, fmt.Errorf("invalid public key size: expected %d bytes, got %d", ed25519.PublicKeySize, len(publicKeyBytes))
	}

	return ed25519.PublicKey(publicKeyBytes), nil
}

// decodeP2PId converts hex-encoded p2pId in the JWT claim to p2ptypes.PeerID
func (v *NodeJWTAuthenticator) decodeP2PId(p2pIdHex string) (p2ptypes.PeerID, error) {
	var peerID p2ptypes.PeerID
	err := peerID.UnmarshalText([]byte(p2pIdHex))
	if err != nil {
		return p2ptypes.PeerID{}, fmt.Errorf("failed to unmarshal PeerID: %w", err)
	}
	return peerID, nil
}

// verifyJWTSignature prove the JWT signature is signed by the node's private key.
// DEV NOTE: The designed JWT method is asymmetric Ed25519, @nodeauth/node_jwt_signer.go.
func (v *NodeJWTAuthenticator) verifyJWTSignature(tokenString string, publicKey ed25519.PublicKey) error {

	token, err := v.parser.Parse(tokenString, func(token *jwt.Token) (interface{}, error) {
		// Ensure the signing method is Ed25519
		if _, ok := token.Method.(*jwt.SigningMethodEd25519); !ok {
			return nil, fmt.Errorf("unexpected signing method: %v", token.Header["alg"])
		}

		return publicKey, nil
	})

	if err != nil {
		return fmt.Errorf("signature verification failed: %w", err)
	}

	if !token.Valid {
		return fmt.Errorf("token is invalid after signature verification")
	}

	return nil
}

// verifyRequestDigest ensures the request hasn't been tampered with by verifying the digest in the JWT claim.
func (v *NodeJWTAuthenticator) verifyRequestDigest(claims *NodeJWTClaims, originalRequest any) error {
	// Calculate expected digest using the same method as the client
	expectedDigest := utils.CalculateRequestDigest(originalRequest)

	if claims.Digest != expectedDigest {
		return fmt.Errorf("digest mismatch: got %s, want %s", claims.Digest, expectedDigest)
	}

	return nil
}
